mapper.select:
/* 게시물 목록 검색 */ SELECT b.board_code ,b.board_category ,bc.category_name ,u.user_id ,b.board_title ,b.board_content ,b.board_file ,b.board_tag ,b.fix_check ,b.view_amount ,b.like_amount ,b.reg_date ,b.modify_date ,b.report_amount ,b.save_point FROM tb_board AS b INNER JOIN tb_board_category AS bc ON b.board_category = bc.board_category INNER JOIN tb_user AS u ON b.user_id = u.user_id
${searchKey2} = #{searchCateName}
AND ${searchKey} LIKE CONCAT('%', #{searchValue}, '%')
AND b.reg_date > (#{firstDate}) AND b.reg_date < (#{lastDate})
ORDER BY b.reg_date DESC;

mapper.update:
        UPDATE tb_board
        SET
         
			,board_category = #{boardCategory}
			,user_id = #{userId}
			,board_title = #{boardTitle}
			,board_content = #{boardContent}
			,board_file = #{boardFile}
			,board_tag = #{boardTag}
			,fix_check = #{fixCheck}
			,view_amount = #{viewAmount}
			,like_amount = #{likeAmount}
			,modify_date = NOW()
			,report_amount = #{reportAmount}
			,save_point = #{savePoint}
		
        WHERE
            board_code = #{boardCode}

mapper.select:
/* 게시물 좋아요 검색 */ SELECT l.board_like ,b.board_category ,bc.category_name ,b.board_title ,l.board_code ,l.user_id ,l.like_reg_date FROM tb_board_like AS l JOIN tb_board AS b ON l.board_code = b.board_code JOIN tb_board_category AS bc ON b.board_category = bc.board_category JOIN tb_user AS u ON l.user_id = u.user_id
${searchKey2} = #{searchCateName}
AND ${searchKey} LIKE CONCAT('%', #{searchValue}, '%')
AND l.like_reg_date > (#{firstDate}) AND l.like_reg_date < (#{lastDate})
ORDER BY l.like_reg_date DESC;

/* 게시물 댓글 조회 */ SELECT co.board_comment ,b.board_category ,b.board_title ,bc.category_name ,co.board_code ,u.user_id ,co.comment_content ,co.reg_date ,co.modify_date ,co.report_amount FROM tb_board_comment AS co INNER JOIN tb_board AS b ON co.board_code = b.board_code INNER JOIN tb_board_category AS bc ON b.board_category = bc.board_category INNER JOIN tb_user AS u ON co.user_id = u.user_id
${searchKey2} = #{searchCateName}
AND ${searchKey} LIKE CONCAT('%', #{searchValue}, '%')
AND co.reg_date > (#{firstDate}) AND co.reg_date < (#{lastDate})
ORDER BY co.reg_date DESC;
