mapper.select:
SELECT o.order_num ,od.order_detail_code ,o.buyer_id ,g.goods_name ,o.coupon_price ,o.use_point ,od.save_point ,od.order_price ,od.order_amount ,o.order_status ,o.order_date FROM tb_order AS o INNER JOIN tb_order_detail AS od ON o.order_num = od.order_num INNER JOIN tb_goods AS g ON od.goods_code = g.goods_code
${sk} LIKE CONCAT('%', #{sv},'%')
ORDER BY o.order_num;

SELECT * FROM (SELECT o.order_num ,od.order_detail_code ,od.goods_code ,g.goods_name ,o.buyer_id ,p.payment_method ,cp.card_company ,cp.card_number ,od.order_price ,cp.approve_price ,cp.approve_date FROM tb_order AS o INNER JOIN tb_order_detail AS od ON o.order_num = od.order_num INNER JOIN tb_goods AS g ON od.goods_code = g.goods_code INNER JOIN tb_payment AS p ON od.order_num = p.order_num INNER JOIN tb_card_payment AS cp ON p.payment_group_code = cp.card_payment_code WHERE od.order_detail_code = #{orderDetailCode}) AS a
UNION
SELECT o.order_num ,od.order_detail_code ,od.goods_code ,g.goods_name ,o.buyer_id ,p.payment_method ,ap.refund_bank ,ap.refund_account ,od.order_price ,ap.approve_price ,ap.approve_date FROM tb_order AS o INNER JOIN tb_order_detail AS od ON o.order_num = od.order_num INNER JOIN tb_goods AS g ON od.goods_code = g.goods_code INNER JOIN tb_payment AS p ON od.order_num = p.order_num INNER JOIN tb_account_payment AS ap ON p.payment_group_code = ap.account_payment_code WHERE od.order_detail_code = #{orderDetailCode};

SELECT * FROM (SELECT o.order_num ,od.order_detail_code ,od.goods_code ,g.goods_name ,o.buyer_id ,p.payment_method ,cp.card_company ,cp.card_number ,od.order_price ,cp.approve_price ,cp.approve_date FROM tb_order AS o INNER JOIN tb_order_detail AS od ON o.order_num = od.order_num INNER JOIN tb_goods AS g ON od.goods_code = g.goods_code INNER JOIN tb_payment AS p ON od.order_num = p.order_num INNER JOIN tb_card_payment AS cp ON p.payment_group_code = cp.card_payment_code) AS a
UNION
SELECT o.order_num ,od.order_detail_code ,od.goods_code ,g.goods_name ,o.buyer_id ,p.payment_method ,ap.refund_bank ,ap.refund_account ,od.order_price ,ap.approve_price ,ap.approve_date FROM tb_order AS o INNER JOIN tb_order_detail AS od ON o.order_num = od.order_num INNER JOIN tb_goods AS g ON od.goods_code = g.goods_code INNER JOIN tb_payment AS p ON od.order_num = p.order_num INNER JOIN tb_account_payment AS ap ON p.payment_group_code = ap.account_payment_code ORDER BY order_detail_code;
