mapper.update:
    	/* 자주 묻는 질문 수청 처리 */
    	UPDATE tb_regular_ask -- 자주 묻는 질문 테이블
     	
     		-- 자주 묻는 질문 카테고리
			 -- 값이 없거나 값이 공백일 경우 수정할 수 없다.
		    	regular_ask_category= #{regularAskCategory}
			
			-- 제목
			 -- 값이 없거나 값이 공백일 경우 수정할 수 없다.
				,regular_ask_title = #{regularAskTitle}
			
			-- 내용
			 -- 값이 없거나 값이 공백일 경우 수정할 수 없다.
				,regular_ask_content = #{regularAskContent}
			
			-- 사용 여부 
			 -- 값이 없거나 값이 공백일 경우 수정할 수 없다. 
				,use_check = #{useCheck}
			
			-- 수정일
			,update_date = NOW() 
		
			WHERE 
				regular_ask=#{regularAskPK};
