		,#{approveId}
		,0
		,#{startSuspend}
		,#{endSuspend}
		,#{suspendResult}
		,#{suspendReason})

mapper.update:
/*정지 리스트 수정 */
UPDATE tb_user_suspend
 
	
		 suspend_result= #{suspendResult}
	
	
		,suspend_reason = #{suspendReason}
	

	WHERE 
		user_suspend_code = #{userSuspendCode}

mapper.select:
/* 특정 기준 목록 검색 */ SELECT r.report_code ,r.report_reason ,r.report_date ,r.user_id ,r.use_check ,r.penalty_point FROM tb_report_rule AS r
${sk} LIKE CONCAT('%',#{sv},'%')
AND r.report_date > (#{fd}) AND r.report_date< (#{td})

mapper.insert:
/* 벌점 기준 목록 등록 (관리자) */
INSERT INTO tb_report_rule
	(report_code
	 ,report_reason 
	 ,report_date 
	 ,user_id
	 ,use_check
	 ,penalty_point)
VALUES 
	(#{reportCode}
	,#{reportReason}
	,NOW()
	,#{userId}
	,#{useCheck}
	,#{penaltyPoint})

mapper.delete:
/*벌전 기준 목록 삭제*/
DELETE 
FROM
	tb_report_rule
WHERE 
	report_code = #{reportCode}

mapper.update:
/*벌점 기준 목록 수정 */
UPDATE tb_report_rule
 
	
		 report_reason= #{reportReason}
	
		,report_date = NOW()
		,user_id = #{userId}
	
		,use_check = #{useCheck}
	
		
	
		,penalty_point = #{penaltyPoint}
	

	WHERE 
		report_code = #{reportCode}
