mapper.select:
/* 회원 목록 조회 */ SELECT u.user_id ,u.user_pw ,u.user_name ,u.user_gender ,u.user_nickname ,u.user_address ,u.user_phone ,u.user_email ,u.user_right ,r.right_status ,u.user_reg_date ,u.user_update_date ,u.user_status ,u.user_penalty ,u.user_point ,u.user_info_keep ,u.user_level FROM tb_user AS u INNER JOIN tb_right AS r ON u.user_right = r.user_right;
AND ${searchKey} LIKE CONCAT('%', #{searchValue}, '%');
ORDER BY u.user_id

SELECT IF(COUNT(*) = 1, user_pw, 'none')
FROM tb_user
WHERE user_id = #{userId};
