mapper.select:
/* 해당 상품이 찜 목록에 있는지 확인 */
SELECT
	count(goods_code)
FROM
	tb_wishlist
WHERE
	buyer_id = #{buyerId}
AND
	goods_code = #{goodsCode}

mapper.insert:
/* 찜 추가  */
insert into tb_wishlist
	(wish_code, goods_code, buyer_id, reg_date)
values
	(#{wishCode}, #{goodsCode}, #{buyerId}, NOW());

mapper.select:
/* 찜 추가시 코드 증가*/
SELECT	MAX(wish_code)
FROM	tb_wishlist
WHERE	buyer_Id = #{buyerId};

mapper.delete:
/* 찜 목록에서 찜 삭제 */
DELETE
FROM 
	tb_wishlist
WHERE
	wish_code = #{wishCode}
AND
	buyer_id = #{buyerId}
